\begin{thmenv}[Marcinkiewicz-Zygmund inequality]\label{mzi} Let $m\geq 1$. If $f:G\to \mathbb {R}$ is such that $\mathbb {E}_x f(x)=0$ and $\left\lvert f(x)\right\rvert \leq 2$ for all $x$ then 

\[ \mathbb {E}_{x_1,\ldots ,x_n} \left\lvert \sum _{i=1}^n f(x_i)\right\rvert ^{2m} \leq (8mn)^{m}. \]

\end{thmenv}

\begin{thmenv} \label{random_approx_expect} Let $\epsilon >0$ and $m\geq 1$. Let $A\subseteq G$ and $f:G\to \{ 0,1\} $. If $k\geq 256m\epsilon ^{-2}$ then the set 

\[  \mathbb {E}_{a_1,\ldots ,a_k\in A} \lVert  \mu _{\vec{}}{a}*f - \mu _A*f \rVert _{2m}^{2m} \leq (\epsilon /2)^{2m}. \]

\end{thmenv}

\begin{thmenv} \label{3aps_in_ff} If $A\subseteq \mathbb {F}_q^n$ has only trivial three-term arithmetic progressions then 

\[ \alpha \leq \exp (-n^{1/9}). \]

\end{thmenv}

 egin{thmenv} \label{unbalancing} Let $\epsilon \in (0,1)$ and $
u :G	o \mathbb {R}_{\geq 0}$ be some probability measure such that $\widehat{
u }\geq 0$. Let $f:G	o \mathbb {R}$. If $\lVert f\circ fVert _{p(
u )}\geq \epsilon $ for some $p\geq 1$ then $\lVert f\circ f+1Vert _{p'(
u )}\geq 1+	frac {1}{2}\epsilon $ for some $p'\ll _\epsilon p$. 

\end{thmenv}

\begin{thmenv} \label{nonneg_moments} For any function $f:G\to \mathbb {R}$ and integer $k\geq 0$ 

\[ \mathbb {E}_x f\circ f(x)^k\geq 0. \]

\end{thmenv}

\begin{thmenv} \label{global_dichotomy} Let $\epsilon >0$. If $A,C\subseteq G$, where $C$ has density at least $\gamma $, then either 

\begin{enumerate} \item $\left\lvert \langle \mu _A\ast \mu _A,\mu _C\rangle -1\right\rvert \leq \epsilon $ or 

\item $\lVert \mu _A\circ \mu _A-1\rVert _p \geq \epsilon /2$ for some $p\ll \log (2.\gamma )$. 

\end{enumerate}

\end{thmenv}

\begin{thmenv} \label{3aps_in_ff_count} If $A\subseteq \mathbb {F}_q^n$ has density $\alpha $ then 

\[ \langle 1_{A}\ast 1_{A},1_{2\cdot A}\rangle \gg \exp (-O(\log (1/\alpha )^9)). \]

\end{thmenv}

