\[ \lvert T\rvert \geq K^{-4096\lceil \mathcal{L}{\eta }\rceil k^2\epsilon ^{-2}}\lvert S\rvert  \]

 such that 

\[ \|  \mu _T^{(k)}\ast \mu _A\ast 1_B\ast \mu _C-\mu _A\ast 1_B\ast \mu _C\| _{\infty }\leq \epsilon . \]

\end{thmenv}

\begin{thmenv}[Marcinkiewicz-Zygmund inequality] \label{mzi} \lean{other_marcinkiewicz_zygmund'} \leanok Let $m\geq 1$. If $f:G\to \mathbb {R}$ is such that $\mathbb {E}_x f(x)=0$ and $\left\lvert f(x)\right\rvert \leq 2$ for all $x$ then 

\[ \mathbb {E}_{x_1,\ldots ,x_n} \left\lvert \sum _{i=1}^n f(x_i)\right\rvert ^{2m} \leq (4mn)^{m}. \]

\end{thmenv}

\begin{thmenv} \label{ast_expand} \lean{balance_conv} \leanok For any function $f$ with $\sum f(x)=1$ 

\[ f\ast f-1/N = (f-1/N)\ast (f-1/N). \]

\end{thmenv}

\begin{thmenv} \label{diss_span} \uses{dissociated} \lean{finset.diss_span} \leanok If $A\subseteq G$ contains no dissociated set with $\geq K+1$ elements then there is $A'\subseteq A$ of size $\left\lvert A'\right\rvert \leq K$ such that 

\[ A\subseteq \left\{  \sum _{a\in A'}c_aa : c_a\in \{ -1,0,1\}  \right\} . \]

\end{thmenv}

\begin{thmenv} \label{nonneg_moments} \lean{pow_inner_nonneg} \leanok For any function $f:G\to \mathbb {R}$ and integer $k\geq 0$ 

\[ \mathbb {E}_x f\circ f(x)^k\geq 0. \]

\end{thmenv}

\begin{thmenv} \label{circ_expand} \lean{balance_dconv} \leanok For any function $f$ with $\sum f(x)=1$ 

\[ f\circ f-1/N = (f-1/N)\circ (f-1/N). \]

\end{thmenv}

\begin{thmenv} \label{lots_of_diagonals} \lean{big_shifts} \leanok Let $A\subseteq G$ and $k\geq 1$ and $L\subseteq A^k$. Then there exists some ${\vec{a}}\in L$ such that 

